

public class Star1Reverse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i,j,k;
		for(i=5; i>=1; i--)
		{
			
			
		   for(j=i; j<=4; j++)
		   {
		    System.out.print(" ");
		   }
		   for(k=1; k<(2*i); k++)
		   {
		   System.out.print("*");	
		   }
		
		System.out.println();
		   }

	}

}
